                        <option value="">Выберите</option>
                        <option value="yes">Возможен</option>
                        <option value="no">Нет</option>
                      </select>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );

      case 'add-transport':
        return (
          <div className="homepage-form-container">
            <div className="homepage-form-header">
              <div className="homepage-form-header-left">
                <div className="homepage-form-header-icon">
                  <svg width="50" height="50" viewBox="0 0 50 50" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="25" cy="24.999" r="20.833" fill="#EEF4F7"/>
                    <path d="M25 18.055c.48 0 .868.388.868.868v5.208h5.208a.868.868 0 1 1 0 1.736h-5.208v5.209a.868.868 0 1 1-1.736 0v-5.209h-5.208a.868.868 0 0 1 0-1.736h5.208v-5.208c0-.48.389-.868.868-.868" fill="#4472B8"/>
                  </svg>
                </div>
                <div className="homepage-form-header-text">
                  <h2>Добавление транспорта</h2>
                  <p>Укажите параметры транспорта и контактную информацию.</p>
                </div>
              </div>
            </div>
          </div>
