import { useState, useEffect } from 'react';
import { getGlobalTestDB } from '../data/testData';

export interface CurrentUser {
  id: string;
  phone: string;
  firstName?: string;
  lastName?: string;
  email?: string;
}

export const useCurrentUser = (): CurrentUser | null => {
  const [currentUser, setCurrentUser] = useState<CurrentUser | null>(null);

  useEffect(() => {
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage
    const storedUser = localStorage.getItem('currentUser');
    console.log('useCurrentUser: storedUser =', storedUser);

    if (storedUser) {
      try {
        const userData = JSON.parse(storedUser);
        console.log('useCurrentUser: parsed userData =', userData);

        // –ï—Å–ª–∏ —É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ—Ç ID, —Å–æ–∑–¥–∞–µ–º –µ–≥–æ
        if (!userData.id) {
          console.log('‚ö†Ô∏è –£ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ—Ç ID, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π...');
          const updatedUserData = {
            ...userData,
            id: `user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
          };

          // –û–±–Ω–æ–≤–ª—è–µ–º –≤ localStorage
          localStorage.setItem('currentUser', JSON.stringify(updatedUserData));
          console.log('‚úÖ –ù–æ–≤—ã–π ID —Å–æ–∑–¥–∞–Ω:', updatedUserData.id);
          console.log('üîç useCurrentUser: updated userData =', updatedUserData);

          setCurrentUser(updatedUserData);
        } else {
          console.log('‚úÖ –£ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è —É–∂–µ –µ—Å—Ç—å ID:', userData.id);
          console.log('üîç useCurrentUser: existing userData =', userData);
          setCurrentUser(userData);
        }
      } catch (error) {
        console.warn('–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', error);
      }
    } else {
      console.log('useCurrentUser: –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ localStorage');
    }
  }, []);

  return currentUser;
};
