import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Header from './Header';
import LeftSidebar from './LeftSidebar';
import { useCurrentUser } from '../hooks/useCurrentUser';
import '../css/left-sidebar.css';
import '../css/homepage.css';

const Homepage: React.FC = () => {
  const location = useLocation();
  const [activeForm, setActiveForm] = useState<'cards' | 'add-cargo' | 'add-transport'>('cards');
  const [loadingCity, setLoadingCity] = useState('');
  const [unloadingCity, setUnloadingCity] = useState('');
  const [showLoadingSuggestions, setShowLoadingSuggestions] = useState(false);
  const [showUnloadingSuggestions, setShowUnloadingSuggestions] = useState(false);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ð´Ð°Ñ‚ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
  const [loadingStartDate, setLoadingStartDate] = useState('');
  const [loadingEndDate, setLoadingEndDate] = useState('');
  const [dateError, setDateError] = useState('');
  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹ "Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð³Ñ€ÑƒÐ·"
  const [showCargoDimensions, setShowCargoDimensions] = useState(false);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹ "Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ñ‚Ñ€Ð°Ð½ÑÐ¿Ð¾Ñ€Ñ‚"
  const [showTransportDimensions, setShowTransportDimensions] = useState(false);

  // Ð”Ð°Ð½Ð½Ñ‹Ðµ Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  const currentUser = useCurrentUser();

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ select ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð² (Ð¾Ð±Ñ‰Ð¸Ðµ Ð´Ð»Ñ Ð¾Ð±ÐµÐ¸Ñ… Ñ„Ð¾Ñ€Ð¼)
  const [selectedValues, setSelectedValues] = useState({
    loadingType: '',
    cargoType: '',
    vehicleType: '',
    reloadType: '',
    paymentMethod: '',
    paymentTerm: '',
    bargain: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ð²ÑÐµÑ… Ð¿Ð¾Ð»ÐµÐ¹ Ñ„Ð¾Ñ€Ð¼
  const [formData, setFormData] = useState({
    // ÐžÐ±Ñ‰Ð¸Ðµ Ð¿Ð¾Ð»Ñ
    loadingStartDate: '',
    loadingEndDate: '',
    loadingCity: '',
    unloadingCity: '',

    // ÐŸÐ¾Ð»Ñ Ð´Ð»Ñ Ð³Ñ€ÑƒÐ·Ð°
    cargoWeight: '',
    cargoVolume: '',
    vehicleCount: '',
    cargoLength: '',
    cargoWidth: '',
    cargoHeight: '',
    cargoPrice: '',
    cargoCurrency: 'USD',

    // ÐŸÐ¾Ð»Ñ Ð´Ð»Ñ Ñ‚Ñ€Ð°Ð½ÑÐ¿Ð¾Ñ€Ñ‚Ð°
    transportWeight: '',
    transportVolume: '',
    transportLength: '',
    transportWidth: '',
    transportHeight: '',
    transportPrice: '',
    transportCurrency: 'USD',

    // ÐšÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ð½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ
    additionalPhone: '',
    email: '',

    // Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ
    additionalInfo: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸
  const [showCard, setShowCard] = useState(false);
  const [currentCard, setCurrentCard] = useState<any>(null);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð²Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ð¸
  const [validationErrors, setValidationErrors] = useState<{[key: string]: boolean}>({});
  const [shakeFields, setShakeFields] = useState<{[key: string]: boolean}>({});
  const [deletingCardId, setDeletingCardId] = useState<number | null>(null);

    // Ð‘Ð°Ð·Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð³Ð¾Ñ€Ð¾Ð´Ð¾Ð²
  const citiesDatabase = [
    'ÐšÐ¸ÐµÐ², Ð£ÐºÑ€Ð°Ð¸Ð½Ð°',
    'ÐšÐ¸ÑˆÐ¸Ð½ÐµÐ², ÐœÐ¾Ð»Ð´Ð¾Ð²Ð°',
    'ÐšÐ¸Ñ€Ð¾Ð², Ð Ð¾ÑÑÐ¸Ñ',
    'ÐšÐ¸Ñ€Ð¾Ð²Ð¾Ð³Ñ€Ð°Ð´, Ð£ÐºÑ€Ð°Ð¸Ð½Ð°',
    'ÐœÐ¾ÑÐºÐ²Ð°, Ð Ð¾ÑÑÐ¸Ñ',
    'Ð¡Ð°Ð½ÐºÑ‚-ÐŸÐµÑ‚ÐµÑ€Ð±ÑƒÑ€Ð³, Ð Ð¾ÑÑÐ¸Ñ',
    'Ð¢Ð°ÑˆÐºÐµÐ½Ñ‚, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'Ð¡Ð°Ð¼Ð°Ñ€ÐºÐ°Ð½Ð´, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'Ð‘ÑƒÑ…Ð°Ñ€Ð°, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'ÐÐ»Ð¼Ð°Ñ‚Ñ‹, ÐšÐ°Ð·Ð°Ñ…ÑÑ‚Ð°Ð½',
    'ÐÑÑ‚Ð°Ð½Ð°, ÐšÐ°Ð·Ð°Ñ…ÑÑ‚Ð°Ð½',
    'ÐœÐ¸Ð½ÑÐº, Ð‘ÐµÐ»Ð°Ñ€ÑƒÑÑŒ',
    'Ð’Ð¸Ð»ÑŒÐ½ÑŽÑ, Ð›Ð¸Ñ‚Ð²Ð°',
    'Ð Ð¸Ð³Ð°, Ð›Ð°Ñ‚Ð²Ð¸Ñ',
    'Ð¢Ð°Ð»Ð»Ð¸Ð½, Ð­ÑÑ‚Ð¾Ð½Ð¸Ñ'
  ];

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ñ€Ð°ÑÑ‡ÐµÑ‚Ð° Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸ Ð½Ð°Ð·Ð°Ð´
  const getTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return 'Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ñ‡Ñ‚Ð¾';
    if (diffInMinutes < 60) return `${diffInMinutes} ${diffInMinutes === 1 ? 'Ð¼Ð¸Ð½ÑƒÑ‚Ñƒ' : diffInMinutes < 5 ? 'Ð¼Ð¸Ð½ÑƒÑ‚Ñ‹' : 'Ð¼Ð¸Ð½ÑƒÑ‚'} Ð½Ð°Ð·Ð°Ð´`;

    const diffInHours = Math.floor(diffInMinutes / 60);
