import React, { useState } from 'react';
import Header from './Header';
import LeftSidebar from './LeftSidebar';
import '../css/left-sidebar.css';

const Homepage: React.FC = () => {
  const [activeForm, setActiveForm] = useState<'cards' | 'add-cargo' | 'add-transport'>('cards');

  const handleAddCargo = () => {
    setActiveForm('add-cargo');
  };

  const handleAddTransport = () => {
    setActiveForm('add-transport');
  };

  const handleBackToCards = () => {
    setActiveForm('cards');
  };

  const renderContent = () => {
    switch (activeForm) {
      case 'add-cargo':
        return (
          <div className="form-container">
            <div className="form-header">
              <h2>Добавить груз</h2>
              <button onClick={handleBackToCards} className="back-button">
                ← Назад к карточкам
              </button>
            </div>
            <div className="form-placeholder">
              <p>Здесь будут инпуты для добавления груза</p>
              <p>Информацию предоставите позже</p>
            </div>
          </div>
        );

      case 'add-transport':
        return (
          <div className="form-container">
            <div className="form-header">
              <h2>Добавить транспорт</h2>
              <button onClick={handleBackToCards} className="back-button">
                ← Назад к карточкам
              </button>
            </div>
            <div className="form-placeholder">
              <p>Здесь будут инпуты для добавления транспорта</p>
              <p>Информацию предоставите позже</p>
            </div>
          </div>
        );

      default:
        return (
          <div className="cards-container">
            <h3>Карточки контента</h3>
            <p>Здесь будут размещены карточки, которые вы укажете</p>
          </div>
        );
    }
  };

  return (
    <>
      <Header />
      <div className="homepage-container container">
        <LeftSidebar 
          onAddCargo={handleAddCargo}
          onAddTransport={handleAddTransport}
        />
        <div className="homepage-content">
          {renderContent()}
        </div>
      </div>
    </>
  );
};

export default Homepage;
