import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { AnimatePresence } from 'framer-motion';
import Login from './components/Login';
import Registration from './components/Registration';
import ForgotPassword from './components/ForgotPassword';
import Dashboard from './components/Dashboard';
import Homepage from './components/Homepage';
import './App.css';

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏ —Ä–æ—É—Ç–∏–Ω–≥–∞
const RouteDebugger: React.FC = () => {
  const location = useLocation();

  React.useEffect(() => {
    console.log('üîç DEBUG RouteDebugger: Location changed to:', location);
    console.log('üîç DEBUG RouteDebugger: pathname =', location.pathname);
    console.log('üîç DEBUG RouteDebugger: search =', location.search);
    console.log('üîç DEBUG RouteDebugger: hash =', location.hash);
  }, [location]);

  return null;
};

function App() {
  // –î–æ–±–∞–≤–ª—è–µ–º –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
  console.log('üîç DEBUG: NODE_ENV =', process.env.NODE_ENV);
  console.log('üîç DEBUG: process.env =', process.env);

  // –ë–æ–ª–µ–µ —è–≤–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —Ä–µ–∂–∏–º–∞
  const isDevelopment = process.env.NODE_ENV === 'development' || !process.env.NODE_ENV;
  const isProduction = process.env.NODE_ENV === 'production';

  // –ò—Å–ø—Ä–∞–≤–ª—è–µ–º –ª–æ–≥–∏–∫—É basename - —É–±–∏—Ä–∞–µ–º GitHub Pages –ø—É—Ç—å –¥–ª—è development
  // –í development —Ä–µ–∂–∏–º–µ –≤—Å–µ–≥–¥–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º –∫–æ—Ä–Ω–µ–≤–æ–π –ø—É—Ç—å
  const basename = isDevelopment ? '' : '/logistics-react';

  console.log('üîç DEBUG: isDevelopment =', isDevelopment);
  console.log('üîç DEBUG: isProduction =', isProduction);
  console.log('üîç DEBUG: basename =', basename);
  console.log('üîç DEBUG: Current URL should be accessed at:', isDevelopment ? 'http://localhost:3000' : 'https://avtnmv.github.io/logistics-react');

  return (
    <Router basename={basename}>
      <div className="App">
        <RouteDebugger />
        <AnimatePresence mode="wait">
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/registration" element={<Registration />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/homepage" element={<Homepage />} />
          </Routes>
        </AnimatePresence>
      </div>
    </Router>
  );
}

export default App;
