import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Header from './Header';
import LeftSidebar from './LeftSidebar';
import { useCurrentUser } from '../hooks/useCurrentUser';
import '../css/left-sidebar.css';
import '../css/homepage.css';

const Homepage: React.FC = () => {
  const location = useLocation();
  const [activeForm, setActiveForm] = useState<'cards' | 'add-cargo' | 'add-transport'>('cards');
  const [loadingCity, setLoadingCity] = useState('');
  const [unloadingCity, setUnloadingCity] = useState('');
  const [showLoadingSuggestions, setShowLoadingSuggestions] = useState(false);
  const [showUnloadingSuggestions, setShowUnloadingSuggestions] = useState(false);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ð´Ð°Ñ‚ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
  const [loadingStartDate, setLoadingStartDate] = useState('');
  const [loadingEndDate, setLoadingEndDate] = useState('');
  const [dateError, setDateError] = useState('');
  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹ "Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð³Ñ€ÑƒÐ·"
  const [showCargoDimensions, setShowCargoDimensions] = useState(false);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹ "Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ñ‚Ñ€Ð°Ð½ÑÐ¿Ð¾Ñ€Ñ‚"
  const [showTransportDimensions, setShowTransportDimensions] = useState(false);

  // Ð”Ð°Ð½Ð½Ñ‹Ðµ Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  const currentUser = useCurrentUser();

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ select ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð² (Ð¾Ð±Ñ‰Ð¸Ðµ Ð´Ð»Ñ Ð¾Ð±ÐµÐ¸Ñ… Ñ„Ð¾Ñ€Ð¼)
  const [selectedValues, setSelectedValues] = useState({
    loadingType: '',
    cargoType: '',
    vehicleType: '',
    reloadType: '',
    paymentMethod: '',
    paymentTerm: '',
    bargain: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð´Ð»Ñ Ð²ÑÐµÑ… Ð¿Ð¾Ð»ÐµÐ¹ Ñ„Ð¾Ñ€Ð¼
  const [formData, setFormData] = useState({
    // ÐžÐ±Ñ‰Ð¸Ðµ Ð¿Ð¾Ð»Ñ
    loadingStartDate: '',
    loadingEndDate: '',
    loadingCity: '',
    unloadingCity: '',

    // ÐŸÐ¾Ð»Ñ Ð´Ð»Ñ Ð³Ñ€ÑƒÐ·Ð°
    cargoWeight: '',
    cargoVolume: '',
    vehicleCount: '',
    cargoLength: '',
    cargoWidth: '',
    cargoHeight: '',
    cargoPrice: '',
    cargoCurrency: 'USD',

    // ÐŸÐ¾Ð»Ñ Ð´Ð»Ñ Ñ‚Ñ€Ð°Ð½ÑÐ¿Ð¾Ñ€Ñ‚Ð°
    transportWeight: '',
    transportVolume: '',
    transportLength: '',
    transportWidth: '',
    transportHeight: '',
    transportPrice: '',
    transportCurrency: 'USD',

    // ÐšÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ð½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ
    additionalPhone: '',
    email: '',

    // Ð”Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ
    additionalInfo: ''
  });

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸
  const [showCard, setShowCard] = useState(false);
  const [currentCard, setCurrentCard] = useState<any>(null);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð´Ð»Ñ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð²Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ð¸
  const [validationErrors, setValidationErrors] = useState<{[key: string]: boolean}>({});
  const [shakeFields, setShakeFields] = useState<{[key: string]: boolean}>({});
  const [deletingCardId, setDeletingCardId] = useState<number | null>(null);

  // Ð‘Ð°Ð·Ð° Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð³Ð¾Ñ€Ð¾Ð´Ð¾Ð²
  const citiesDatabase = [
    'ÐšÐ¸ÐµÐ², Ð£ÐºÑ€Ð°Ð¸Ð½Ð°',
    'ÐšÐ¸ÑˆÐ¸Ð½ÐµÐ², ÐœÐ¾Ð»Ð´Ð¾Ð²Ð°',
    'ÐšÐ¸Ñ€Ð¾Ð², Ð Ð¾ÑÑÐ¸Ñ',
    'ÐšÐ¸Ñ€Ð¾Ð²Ð¾Ð³Ñ€Ð°Ð´, Ð£ÐºÑ€Ð°Ð¸Ð½Ð°',
    'ÐœÐ¾ÑÐºÐ²Ð°, Ð Ð¾ÑÑÐ¸Ñ',
    'Ð¡Ð°Ð½ÐºÑ‚-ÐŸÐµÑ‚ÐµÑ€Ð±ÑƒÑ€Ð³, Ð Ð¾ÑÑÐ¸Ñ',
    'Ð¢Ð°ÑˆÐºÐµÐ½Ñ‚, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'Ð¡Ð°Ð¼Ð°Ñ€ÐºÐ°Ð½Ð´, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'Ð‘ÑƒÑ…Ð°Ñ€Ð°, Ð£Ð·Ð±ÐµÐºÐ¸ÑÑ‚Ð°Ð½',
    'ÐÐ»Ð¼Ð°Ñ‚Ñ‹, ÐšÐ°Ð·Ð°Ñ…ÑÑ‚Ð°Ð½',
    'ÐÑÑ‚Ð°Ð½Ð°, ÐšÐ°Ð·Ð°Ñ…ÑÑ‚Ð°Ð½',
    'ÐœÐ¸Ð½ÑÐº, Ð‘ÐµÐ»Ð°Ñ€ÑƒÑÑŒ',
    'Ð’Ð¸Ð»ÑŒÐ½ÑŽÑ, Ð›Ð¸Ñ‚Ð²Ð°',
    'Ð Ð¸Ð³Ð°, Ð›Ð°Ñ‚Ð²Ð¸Ñ',
    'Ð¢Ð°Ð»Ð»Ð¸Ð½, Ð­ÑÑ‚Ð¾Ð½Ð¸Ñ'
  ];

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ Ð¿Ñ€ÐµÐ¾Ð±Ñ€Ð°Ð·Ð¾Ð²Ð°Ð½Ð¸Ñ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ð¹ Ð² Ð¿Ð¾Ð½ÑÑ‚Ð½Ñ‹Ðµ Ð½Ð°Ð·Ð²Ð°Ð½Ð¸Ñ
  const getCargoTypeName = (value: string): string => {
    const cargoTypes: { [key: string]: string } = {
      'pallets': 'Ð“Ñ€ÑƒÐ· Ð½Ð° Ð¿Ð°Ð»Ð»ÐµÑ‚Ð°Ñ…',
      'equipment': 'ÐžÐ±Ð¾Ñ€ÑƒÐ´Ð¾Ð²Ð°Ð½Ð¸Ðµ',
      'construction': 'Ð¡Ñ‚Ñ€Ð¾Ð¹Ð¼Ð°Ñ‚ÐµÑ€Ð¸Ð°Ð»Ñ‹',
      'metal': 'ÐœÐµÑ‚Ð°Ð»Ð»',
      'metal-products': 'ÐœÐµÑ‚Ð°Ð»Ð»Ð¾Ð¿Ñ€Ð¾ÐºÐ°Ñ‚',
      'pipes': 'Ð¢Ñ€ÑƒÐ±Ñ‹',
      'food': 'ÐŸÑ€Ð¾Ð´ÑƒÐºÑ‚Ñ‹',
      'big-bags': 'Ð“Ñ€ÑƒÐ· Ð² Ð±Ð¸Ð³-Ð±ÑÐ³Ð°Ñ…',
      'container': 'ÐšÐ¾Ð½Ñ‚ÐµÐ¹Ð½ÐµÑ€',
      'cement': 'Ð¦ÐµÐ¼ÐµÐ½Ñ‚',
      'bitumen': 'Ð‘Ð¸Ñ‚ÑƒÐ¼',
      'fuel': 'Ð“Ð¡Ðœ',
      'flour': 'ÐœÑƒÐºÐ°',
      'oversized': 'ÐÐµÐ³Ð°Ð±Ð°Ñ€Ð¸Ñ‚',
      'cars': 'ÐÐ²Ñ‚Ð¾Ð¼Ð¾Ð±Ð¸Ð»Ð¸',
      'lumber': 'ÐŸÐ¸Ð»Ð¾Ð¼Ð°Ñ‚ÐµÑ€Ð¸Ð°Ð»Ñ‹',
      'concrete': 'Ð‘ÐµÑ‚Ð¾Ð½Ð½Ñ‹Ðµ Ð¸Ð·Ð´ÐµÐ»Ð¸Ñ',
      'furniture': 'ÐœÐµÐ±ÐµÐ»ÑŒ',
      'other': 'Ð”Ñ€ÑƒÐ³Ð¾Ð¹ Ñ‚Ð¸Ð¿'
    };
    return cargoTypes[value] || value;
  };

  const getVehicleTypeName = (value: string): string => {
    const vehicleTypes: { [key: string]: string } = {
      'tent': 'Ð¢ÐµÐ½Ñ‚',
      'isotherm': 'Ð˜Ð·Ð¾Ñ‚ÐµÑ€Ð¼',
      'refrigerator': 'Ð ÐµÑ„Ñ€Ð¸Ð¶ÐµÑ€Ð°Ñ‚Ð¾Ñ€',
      'flatbed': 'Ð‘Ð¾Ñ€Ñ‚Ð¾Ð²Ð¾Ð¹',
      'car-carrier': 'ÐÐ²Ñ‚Ð¾Ð²Ð¾Ð·',
      'platform': 'ÐŸÐ»Ð°Ñ‚Ñ„Ð¾Ñ€Ð¼Ð°',
      'cement-truck': 'Ð¦ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð²Ð¾Ð·',
      'bitumen-truck': 'Ð‘Ð¸Ñ‚ÑƒÐ¼Ð¾Ð²Ð¾Ð·',
      'fuel-truck': 'Ð‘ÐµÐ½Ð·Ð¾Ð²Ð¾Ð·',
      'flour-truck': 'ÐœÑƒÐºÐ¾Ð²Ð¾Ð·',
      'tow-truck': 'Ð­Ð²Ð°ÐºÑƒÐ°Ñ‚Ð¾Ñ€',
      'timber-truck': 'Ð›ÐµÑÐ¾Ð²Ð¾Ð·',
      'grain-truck': 'Ð—ÐµÑ€Ð½Ð¾Ð²Ð¾Ð·',
      'trailer': 'Ð¢Ñ€Ð°Ð»',
      'dump-truck': 'Ð¡Ð°Ð¼Ð¾ÑÐ²Ð°Ð»',
      'container-truck': 'ÐšÐ¾Ð½Ñ‚ÐµÐ¹Ð½ÐµÑ€Ð¾Ð²Ð¾Ð·',
      'oversized-truck': 'ÐÐµÐ³Ð°Ð±Ð°Ñ€Ð¸Ñ‚',
      'bus': 'ÐÐ²Ñ‚Ð¾Ð±ÑƒÑ',
      'gas-truck': 'Ð“Ð°Ð·Ð¾Ð²Ð¾Ð·',
      'other-truck': 'Ð”Ñ€ÑƒÐ³Ð¾Ð¹ Ñ‚Ð¸Ð¿'
    };
    return vehicleTypes[value] || value;
  };

  const getReloadTypeName = (value: string): string => {
    const reloadTypes: { [key: string]: string } = {
      'no-reload': 'Ð‘ÐµÐ· Ð´Ð¾Ð³Ñ€ÑƒÐ·ÐºÐ¸ (Ð¾Ñ‚Ð´ÐµÐ»ÑŒÐ½Ð¾Ðµ Ð°Ð²Ñ‚Ð¾)',
      'possible-reload': 'Ð’Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ð° Ð´Ð¾Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°'
    };
    return reloadTypes[value] || value;
  };

  // Ð˜Ð·Ð¼ÐµÐ½ÑÐµÐ¼ Ñ†Ð²ÐµÑ‚ Ñ„Ð¾Ð½Ð° Ð¿Ñ€Ð¸ Ð²Ñ…Ð¾Ð´Ðµ Ð² ÐºÐ°Ð±Ð¸Ð½ÐµÑ‚
  useEffect(() => {
    document.body.style.backgroundColor = '#F5F5F5';

    // Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ð±ÐµÐ»Ñ‹Ð¹ Ñ„Ð¾Ð½ Ð¿Ñ€Ð¸ Ñ€Ð°Ð·Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
    return () => {
      document.body.style.backgroundColor = 'white';
    };
  }, []);

  // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸ Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡Ð°ÐµÐ¼ÑÑ Ð½Ð° Ð²ÐºÐ»Ð°Ð´ÐºÑƒ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐµÐº Ð¿Ñ€Ð¸ Ð¿ÐµÑ€ÐµÑ…Ð¾Ð´Ðµ Ð½Ð° /my-transports
  useEffect(() => {
    if (location.pathname === '/my-transports') {
      setActiveForm('cards');
      return;
    }

    if (location.pathname === '/homepage') {
      const params = new URLSearchParams(location.search);
      const formParam = params.get('form');
      if (formParam === 'add-cargo') {
        setActiveForm('add-cargo');
        setShowCargoDimensions(false);
        setShowTransportDimensions(false);
      } else if (formParam === 'add-transport') {
        setActiveForm('add-transport');
        setShowCargoDimensions(false);
        setShowTransportDimensions(false);
      }
    }
  }, [location.pathname, location.search]);

  // ÐœÐ¸Ð³Ñ€Ð°Ñ†Ð¸Ñ ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÑŽÑ‰Ð¸Ñ… ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐµÐº Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ
  useEffect(() => {
    if (currentUser?.id) {
      console.log('=== ÐœÐ˜Ð“Ð ÐÐ¦Ð˜Ð¯ ÐšÐÐ Ð¢ÐžÐ§Ð•Ðš ===');
      console.log('Ð¢ÐµÐºÑƒÑ‰Ð¸Ð¹ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ:', currentUser);

      const allCards = JSON.parse(localStorage.getItem('transportCards') || '[]');
      console.log('Ð’ÑÐµ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð² localStorage:', allCards);

      const userCards = allCards.filter((card: any) => card.userId === currentUser.id);
      console.log('ÐšÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð¿Ð¾ÑÐ»Ðµ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ð¸:', userCards);

      if (userCards.length > 0) {
        // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð² Ð¾Ñ‚Ð´ÐµÐ»ÑŒÐ½Ð¾Ðµ Ñ…Ñ€Ð°Ð½Ð¸Ð»Ð¸Ñ‰Ðµ
        const storageKey = `transportCards_${currentUser.id}`;
        localStorage.setItem(storageKey, JSON.stringify(userCards));
        console.log('ðŸ”„ ÐœÐ¸Ð³Ñ€Ð°Ñ†Ð¸Ñ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐµÐº Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð·Ð°Ð²ÐµÑ€ÑˆÐµÐ½Ð°:', userCards.length, 'ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐµÐº');
        console.log('Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¾ Ð²:', storageKey);
      } else {
        console.log('âš ï¸ ÐšÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½Ñ‹ Ð´Ð»Ñ Ð¼Ð¸Ð³Ñ€Ð°Ñ†Ð¸Ð¸');
      }
    } else {
      console.log('âŒ ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð½Ðµ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½, Ð¼Ð¸Ð³Ñ€Ð°Ñ†Ð¸Ñ Ð½Ðµ Ð²Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ð°');
    }
  }, [currentUser]);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÐºÐ»Ð¸ÐºÐ¾Ð² Ð²Ð½Ðµ Ð¿Ð¾Ð¿-Ð°Ð¿Ð° Ð°Ð²Ñ‚Ð¾ÐºÐ¾Ð¼Ð¿Ð»Ð¸Ñ‚Ð°
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as Element;
      if (!target.closest('.form-field')) {
        setShowLoadingSuggestions(false);
        setShowUnloadingSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleAddCargo = () => {
    setActiveForm('add-cargo');
    // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð³Ð°Ð±Ð°Ñ€Ð¸Ñ‚Ð¾Ð² Ð¿Ñ€Ð¸ Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ð¸
    setShowCargoDimensions(false);
    setShowTransportDimensions(false);
  };

  const handleAddTransport = () => {
    setActiveForm('add-transport');
    // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ð³Ð°Ð±Ð°Ñ€Ð¸Ñ‚Ð¾Ð² Ð¿Ñ€Ð¸ Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ð¸
    setShowCargoDimensions(false);
    setShowTransportDimensions(false);
  };

  const handleShowCards = () => {
    setActiveForm('cards');
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ Ð°Ð²Ñ‚Ð¾ÐºÐ¾Ð¼Ð¿Ð»Ð¸Ñ‚Ð°
  const filterCities = (query: string) => {
    if (!query.trim()) return [];
    return citiesDatabase.filter(city => 
      city.toLowerCase().includes(query.toLowerCase())
    );
  };

  const handleCitySelect = (city: string, isLoading: boolean) => {
    if (isLoading) {
      setLoadingCity(city);
      setFormData(prev => ({ ...prev, loadingCity: city }));
      setShowLoadingSuggestions(false);
    } else {
      setUnloadingCity(city);
      setFormData(prev => ({ ...prev, unloadingCity: city }));
      setShowUnloadingSuggestions(false);
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸ÐºÐ¸ Ð´Ð»Ñ Ð¿Ð¾Ð»ÐµÐ¹ Ð³Ð¾Ñ€Ð¾Ð´Ð¾Ð²
  const handleLoadingCityChange = (value: string) => {
    setLoadingCity(value);
    setFormData(prev => ({ ...prev, loadingCity: value }));
    setShowLoadingSuggestions(value.length > 0);

    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ Ð´Ð»Ñ ÑÑ‚Ð¾Ð³Ð¾ Ð¿Ð¾Ð»Ñ
    if (validationErrors.loadingCity) {
      setValidationErrors(prev => ({ ...prev, loadingCity: false }));
    }
  };

  const handleUnloadingCityChange = (value: string) => {
    setUnloadingCity(value);
    setFormData(prev => ({ ...prev, unloadingCity: value }));
    setShowUnloadingSuggestions(value.length > 0);

    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ Ð´Ð»Ñ ÑÑ‚Ð¾Ð³Ð¾ Ð¿Ð¾Ð»Ñ
    if (validationErrors.unloadingCity) {
      setValidationErrors(prev => ({ ...prev, unloadingCity: false }));
    }
  };

  const handleClickOutside = () => {
    setShowLoadingSuggestions(false);
    setShowUnloadingSuggestions(false);
  };

  const handleSelectChange = (field: string, value: string) => {
    setSelectedValues(prev => ({
      ...prev,
      [field]: value
    }));

    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð¾ÑˆÐ¸Ð±ÐºÑƒ Ð´Ð»Ñ ÑÑ‚Ð¾Ð³Ð¾ Ð¿Ð¾Ð»Ñ
    if (validationErrors[field]) {
      setValidationErrors(prev => ({ ...prev, [field]: false }));
    }
  };

  // Ð’Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ñ Ð´Ð°Ñ‚ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
  const validateDates = (startDate: string, endDate: string): boolean => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const start = new Date(startDate);
    const end = new Date(endDate);

    if (start < today) {
      setDateError('Ð”Ð°Ñ‚Ð° Ð½Ð°Ñ‡Ð°Ð»Ð° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð½Ðµ Ð¼Ð¾Ð¶ÐµÑ‚ Ð±Ñ‹Ñ‚ÑŒ Ñ€Ð°Ð½ÑŒÑˆÐµ ÑÐµÐ³Ð¾Ð´Ð½ÑÑˆÐ½ÐµÐ³Ð¾ Ð´Ð½Ñ');
      return false;
    }

    if (end < start) {
      setDateError('Ð”Ð°Ñ‚Ð° Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð½Ðµ Ð¼Ð¾Ð¶ÐµÑ‚ Ð±Ñ‹Ñ‚ÑŒ Ñ€Ð°Ð½ÑŒÑˆÐµ Ð´Ð°Ñ‚Ñ‹ Ð½Ð°Ñ‡Ð°Ð»Ð°');
      return false;
    }

    setDateError('');
    return true;
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð´Ð°Ñ‚Ñ‹ Ð½Ð°Ñ‡Ð°Ð»Ð°
  const handleStartDateChange = (date: string) => {
    setFormData(prev => ({ ...prev, loadingStartDate: date }));
    if (formData.loadingEndDate && !validateDates(date, formData.loadingEndDate)) {
      setLoadingEndDate('');
      setFormData(prev => ({ ...prev, loadingEndDate: '' }));
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð´Ð°Ñ‚Ñ‹ Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ
  const handleEndDateChange = (date: string) => {
    setFormData(prev => ({ ...prev, loadingEndDate: date }));
    if (formData.loadingStartDate) {
      validateDates(formData.loadingStartDate, date);
    }
  };

  // Ð’Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ñ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ñ… Ð¿Ð¾Ð»ÐµÐ¹ Ð´Ð»Ñ Ð³Ñ€ÑƒÐ·Ð°
  const validateCargoForm = () => {
    const errors: {[key: string]: boolean} = {};
    const requiredFields = [
      'loadingStartDate',
      'loadingEndDate', 
      'loadingCity',
      'unloadingCity',
      'cargoWeight',
      'cargoVolume'
    ];

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð¿Ð¾Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹
    for (const field of requiredFields) {
      if (!formData[field as keyof typeof formData] || formData[field as keyof typeof formData] === '') {
        errors[field] = true;
      }
    }

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ select Ð¿Ð¾Ð»Ñ
    if (!selectedValues.loadingType) {
      errors['loadingType'] = true;
    }
    if (!selectedValues.cargoType) {
      errors['cargoType'] = true;
    }

    setValidationErrors(errors);

    // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ Ð¾ÑˆÐ¸Ð±ÐºÐ¸, Ð·Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸ÑŽ Ñ‚Ñ€ÑÑÐºÐ¸
    if (Object.keys(errors).length > 0) {
      setShakeFields(errors);
      setTimeout(() => setShakeFields({}), 600);
    }

    return Object.keys(errors).length === 0;
  };

  // Ð’Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ñ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ñ… Ð¿Ð¾Ð»ÐµÐ¹ Ð´Ð»Ñ Ñ‚Ñ€Ð°Ð½ÑÐ¿Ð¾Ñ€Ñ‚Ð°
  const validateTransportForm = () => {
    const errors: {[key: string]: boolean} = {};
    const requiredFields = [
      'loadingStartDate',
      'loadingEndDate',
      'loadingCity', 
      'unloadingCity',
      'transportWeight',
      'transportVolume'
    ];

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð¿Ð¾Ð»Ñ Ñ„Ð¾Ñ€Ð¼Ñ‹
    for (const field of requiredFields) {
      if (!formData[field as keyof typeof formData] || formData[field as keyof typeof formData] === '') {
        errors[field] = true;
      }
    }

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ select Ð¿Ð¾Ð»Ñ
    if (!selectedValues.vehicleType) {
      errors['vehicleType'] = true;
    }

    setValidationErrors(errors);

    // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ Ð¾ÑˆÐ¸Ð±ÐºÐ¸, Ð·Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸ÑŽ Ñ‚Ñ€ÑÑÐºÐ¸
    if (Object.keys(errors).length > 0) {
      setShakeFields(errors);
      setTimeout(() => setShakeFields({}), 600);
    }

    return Object.keys(errors).length === 0;
  };

  // Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸
  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ¸
  const deleteCard = (cardId: number) => {
    if (window.confirm('Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ ÑƒÐ´Ð°Ð»Ð¸Ñ‚ÑŒ ÑÑ‚Ñƒ Ð·Ð°ÑÐ²ÐºÑƒ?')) {
      // Ð—Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸ÑŽ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ
      setDeletingCardId(cardId);

      // Ð£Ð´Ð°Ð»ÑÐµÐ¼ ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÑƒ Ñ‡ÐµÑ€ÐµÐ· 300Ð¼Ñ (Ð²Ñ€ÐµÐ¼Ñ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸Ð¸)
      setTimeout(() => {
        if (currentUser?.id) {
          // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð¸Ð· Ñ…Ñ€Ð°Ð½Ð¸Ð»Ð¸Ñ‰Ð° Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
          const storageKey = `transportCards_${currentUser.id}`;
          const userCards = JSON.parse(localStorage.getItem(storageKey) || '[]');
          const updatedUserCards = userCards.filter((card: any) => card.id !== cardId);
          localStorage.setItem(storageKey, JSON.stringify(updatedUserCards));

