        <LeftSidebar 
          onAddCargo={handleAddCargo}
          onAddTransport={handleAddTransport}
          onShowCards={handleShowCards}
          activeForm={activeForm}
          currentUser={currentUser}
        />
        <div className="homepage-content">
          {renderContent()}
        </div>
      </div>
    </>
  );
};

export default Homepage;
