import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import LeftSidebar from './LeftSidebar';
import FormMessage from './FormMessage';
import { useCurrentUser } from '../hooks/useCurrentUser';
import '../css/left-sidebar.css';
import '../css/homepage.css';

const Security: React.FC = () => {
  const navigate = useNavigate();
  const currentUser = useCurrentUser();

  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState<'success' | 'error' | 'info'>('info');
  const [messageVisible, setMessageVisible] = useState(false);

  useEffect(() => {
    document.body.style.backgroundColor = 'rgb(245, 245, 245)';
    return () => {
      document.body.style.backgroundColor = 'white';
    };
  }, []);

  const validateEmail = (value: string) => {
    const re = /^(?:[a-zA-Z0-9_'^&\/+-])+(?:\.(?:[a-zA-Z0-9_'^&\/+-])+)*@(?:(?:[a-zA-Z0-9-]+\.)+[a-zA-Z]{2,})$/;
    return re.test(value);
  };

  const handleSendCode = () => {
    if (!email.trim()) {
      setMessage('Пожалуйста, укажите E-mail');
      setMessageType('error');
      setMessageVisible(true);
      return;
    }

    if (!validateEmail(email.trim())) {
      setMessage('Некорректный E-mail. Проверьте адрес и попробуйте снова.');
      setMessageType('error');
      setMessageVisible(true);
      return;
    }

    // Имитация отправки кода подтверждения на email
    console.log(`✉️ Имитация: Отправлен код подтверждения на ${email}`);
    setMessage(`Код подтверждения отправлен на ${email}`);
    setMessageType('success');
    setMessageVisible(true);
  };

  const handleChangePassword = () => {
    navigate('/forgot-password');
  };

  return (
    <>
      <Header />
      <div className="homepage-container container">
        <LeftSidebar 
          onAddCargo={() => navigate('/homepage?form=add-cargo')}
          onAddTransport={() => navigate('/homepage?form=add-transport')}
          onShowCards={() => navigate('/my-transports')}
          activeForm={'cards'}
          currentUser={currentUser}
        />

        <div className="homepage-content">
          {/* Блок: Добавить E-mail (в стилистике как добавить транспорт) */}
          <div className="homepage-form-header-block">
            <div className="homepage-form-header-icon">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="32" height="32" color="#000000" fill="none">
                <path d="M3 5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2v.382a2 2 0 0 1-.764 1.58l-7 5.385a2 2 0 0 1-2.472 0l-7-5.385A2 2 0 0 1 3 5.382z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M21 8.5V17a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8.5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="homepage-form-header-content">
              <h2>Хотите добавить E-mail?</h2>
              <p>Добавьте электронную почту для дополнительной безопасности и удобства связи.</p>
            </div>
          </div>

          <div className="homepage-form-container">
            <div className="homepage-form-content">
              <div className="form-section">
                <div className="form-row" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end' }}>
                  <div className="form-field" style={{ maxWidth: '405px' }}>
                    <label>E-mail</label>
                    <input 
                      type="email" 
                      className="form-input" 
                      placeholder="example@email.com" 
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                    <div style={{ marginTop: '8px', color: '#666', fontSize: '14px' }}>
                      На указанный адрес будет отправлен код подтверждения. Используйте его для завершения добавления почты.
                    </div>
                  </div>
                  <div className="form-field" style={{ display: 'flex', alignItems: 'flex-end' }}>
                    <button className="submit-cargo-btn" onClick={handleSendCode}>Отправить код</button>
                  </div>
                </div>
              </div>

              <div style={{ marginTop: '16px' }}>
                <FormMessage message={message} type={messageType} isVisible={messageVisible} />
              </div>
            </div>
          </div>

          {/* Блок: Сменить пароль */}
          <div className="homepage-form-container" style={{ marginTop: '24px' }}>
            <div className="homepage-form-content">
              <h3>Хотите сменить пароль?</h3>
              <p style={{ marginTop: '8px' }}>
                Пароли хранятся в зашифрованном виде, поэтому мы не можем отобразить их в настройках. Если вы хотите изменить пароль, нажмите «Сменить пароль».
              </p>

              <div style={{ marginTop: '16px' }}>
                <button className="submit-transport-btn" onClick={handleChangePassword}>Сменить пароль</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Security;
